var validate=function(){var a=true;removeElementsByClass("error");removeClass("error-form");if(document.form.name.value==""){errorElement(document.form.name,"お名前が入力されていません");a=false}if(document.form.furigana.value==""){errorElement(document.form.furigana,"ふりがなが入力されていません");a=false}else{if(!validateKana(document.form.furigana.value)){errorElement(document.form.furigana,"ひらがな以外の文字が入っています");a=false}}if(document.form.email.value==""){errorElement(document.form.email,"メールアドレスが入力されていません");a=false}else{if(!validateMail(document.form.email.value)){errorElement(document.form.email,"メールアドレスが正しくありません");a=false}}if(document.form.tel.value==""){errorElement(document.form.tel,"電話番号が入力されていません");a=false}else{if(!validateNumber(document.form.tel.value)){errorElement(document.form.tel,"半角数字のみを入力してください");a=false}else{if(!validateTel(document.form.tel.value)){errorElement(document.form.tel,"電話番号が正しくありません");a=false}}}if(document.form.item.value==""){errorElement(document.form.item,"お問い合わせ項目が選択されていません");a=false}if(document.form.content.value==""){errorElement(document.form.content,"お問い合わせ内容が入力されていません");a=false}return a};var errorElement=function(a,c){a.className="error-form";var d=document.createElement("div");d.className="error";var b=document.createTextNode(c);d.appendChild(b);a.parentNode.insertBefore(d,a.nextSibling)};var removeElementsByClass=function(a){var b=document.getElementsByClassName(a);while(b.length>0){b[0].parentNode.removeChild(b[0])}};var removeClass=function(a){var b=document.getElementsByClassName(a);while(b.length>0){b[0].className=""}};var validateMail=function(a){if(a.match(/^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/)==null){return false}else{return true}};var validateNumber=function(a){if(a.match(/[^0-9]+/)){return false}else{return true}};var validateTel=function(a){if(a.match(/^[0-9-]{6,13}$/)==null){return false}else{return true}};var validateKana=function(a){if(a.match(/^[ぁ-ん]+$/)==null){return false}else{return true}};